mapper.select:
SELECT filmwishno, memberno, filmno
FROM filmwish
WHERE filmwishno = #{filmwishno}

SELECT COUNT(filmwishno) as cnt
FROM filmwish
WHERE memberno=#{memberno} AND filmno = #{filmno}

mapper.update:
UPDATE filmwish
SET memberno = #{memberno}, filmno = #{filmno}
WHERE filmwishno = #{filmwishno}
