mapper.select:
SELECT languageno, filmno, en, ch, es, fr, ar, rs, pt, de, jp, hi, kr
FROM language
WHERE filmno = #{filmno}

mapper.update:
UPDATE language
SET en=#{en}, ch=#{ch}, es=#{es}, fr=#{fr}, ar=#{ar}, rs=#{rs}, pt=#{pt}, de=#{de}, jp=#{jp}, hi=#{hi}, kr=#{kr}
WHERE languageno = #{languageno}
