mapper.select:
SELECT promono, promotitle, promocontent, promodisc, promovalid, promostart, promoend
FROM promotion
WHERE promono = #{promono}

mapper.update:
UPDATE promotion
SET promotitle=#{promotitle, jdbcType=VARCHAR}, promocontent=#{promocontent, jdbcType=VARCHAR}, promodisc=#{promodisc, jdbcType=INTEGER},
      promovalid=#{promovalid, jdbcType=INTEGER}, promostart=#{promostart, jdbcType=VARCHAR}, promoend=#{promoend, jdbcType=VARCHAR}
WHERE promono = #{promono, jdbcType=INTEGER}
