mapper.select:
SELECT dirfavno, memberno, dirno
FROM directorfav
WHERE dirfavno = #{dirfavno}

SELECT COUNT(dirfavno) as cnt
FROM directorfav
WHERE memberno=#{memberno} AND dirno = #{dirno}

mapper.update:
UPDATE directorfav
SET memberno = #{memberno}, dirno = #{dirno}
WHERE dirfavno = #{dirfavno}
