mapper.select:
SELECT filmfavno, memberno, filmno
FROM filmfav
WHERE filmfavno = #{filmfavno}

SELECT COUNT(filmfavno) as cnt
FROM filmfav
WHERE memberno=#{memberno} AND filmno = #{filmno}

mapper.update:
UPDATE filmfav
SET memberno = #{memberno}, filmno = #{filmno}
WHERE filmfavno = #{filmfavno}
