mapper.select:
SELECT filmgenreno, genreno, filmno
FROM filmgenre
WHERE filmgenreno = #{filmgenreno}

SELECT COUNT(filmgenreno) as cnt
FROM filmgenre
WHERE genreno=#{genreno} AND filmno = #{filmno}

mapper.update:
UPDATE filmgenre
SET genreno = #{genreno}, filmno = #{filmno}
WHERE filmgenreno = #{filmgenreno}

mapper.select:
SELECT COUNT(filmgenreno) as cnt
FROM filmgenre
WHERE filmno=#{filmno}
