mapper.select:
SELECT quesno, questitle, quescontent, quesdate, qcateno, memberno, payno, filmno 
FROM ques
WHERE quesno = #{quesno}

mapper.update:
UPDATE ques
SET questitle = #{questitle}, quescontent = #{quescontent}, qcateno = #{qcateno}, payno = #{payno}, filmno = #{filmno}
WHERE quesno = #{quesno}
